    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String login;
        login = jTextField1.getText();
        int loginNAOW = Integer.parseInt(login);
        if (login.contains("Librarian")) {

            for (int i = 0; i < LibraryMain.LibrarianID.size(); i++) {
                if (loginNAOW == (LibraryMain.LibrarianID.get(i).id)) {

                    new LibrarianGUI().setVisible(true);
                    this.dispose();
                }
            }
        } else if (login.contains("Patron")) {
            for (int i = 0; i < LibraryMain.PatronID.size(); i++) {
                if (loginNAOW == (LibraryMain.PatronID.get(i).id)) {

                    new PatronGUI().setVisible(true);
                    this.dispose();
                }
            }
        } else {
            System.out.println("You done goofed");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

